import * as React from 'react';

export type TextBodyType = "1" | "2";

export type TextBodyColor = "black" | "gray";

export interface TextBodyProps {
    /**
     * The text to appear in the text body
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Which type of text style is wanted
     */
    type?: TextBodyType;
    /**
     * Color of font
     */
    color?: TextBodyColor;
    /**
     * Monospace numbers
     */
    numeric?: boolean;
    /**
     * Italicize the text
     */
    italic?: boolean;
    /**
     * Bold text
     */
    bold?: boolean;
    /**
     * Theme (light/dark mode) used to determine font colour
     */
    theme?: string;
}

declare const TextBody: React.FC<TextBodyProps>;

export default TextBody;

