import * as React from 'react';

export type TextButtonIconPosition = "left" | "right";

export type TextButtonColor = "black" | "blue";

export type TextButtonWeight = "regular" | "bold";

export interface TextButtonProps {
    /**
     * Text inside the button
     */
    children: React.ReactNode;
    /**
     * Icon to the right of button
     */
    Icon: (...args: any[])=>any;
    /**
     * Position of icon
     */
    iconPosition?: TextButtonIconPosition;
    /**
     * Function to be called when button is clicked
     */
    onClick: (...args: any[])=>any;
    /**
     * Additional option styles
     */
    className?: string;
    /**
     * Text button color
     */
    color?: TextButtonColor;
    /**
     * text weight
     */
    weight?: TextButtonWeight;
}

declare const TextButton: React.FC<TextButtonProps>;

export default TextButton;

