import * as React from 'react';

export type TextCaptionColor = "black" | "gray";

export interface TextCaptionProps {
    /**
     * The text to appear in the text caption
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Color of font
     */
    color?: TextCaptionColor;
    /**
     * Monospace numbers
     */
    numeric?: boolean;
    /**
     * Italicize text
     */
    italic?: boolean;
    /**
     * Bold text
     */
    bold?: boolean;
}

declare const TextCaption: React.FC<TextCaptionProps>;

export default TextCaption;

