import * as React from 'react';

export type TextHeadlineColor = "white" | "black";

export type TextHeadlineSize = 18 | 21 | 24 | 28 | 32 | 36 | 48;

export interface TextHeadlineProps {
    /**
     * The text to appear in the text heading
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Color of font
     */
    color?: TextHeadlineColor;
    /**
     * Size of Headline typography
     */
    size?: TextHeadlineSize;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Theme used to determine font colour
     */
    theme?: Object;
}

declare const TextHeadline: React.FC<TextHeadlineProps>;

export default TextHeadline;

