import * as React from 'react';

export type TextIntroductionColor = "white" | "black" | "gray";

export interface TextIntroductionProps {
    /**
     * The text to appear in the text heading
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Color of font
     */
    color?: TextIntroductionColor;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Italicize the text
     */
    italic?: boolean;
    /**
     * Bold text
     */
    bold?: boolean;
}

declare const TextIntroduction: React.FC<TextIntroductionProps>;

export default TextIntroduction;

