import * as React from 'react';

export interface TextLegalProps {
    /**
     * The text to appear in the text Legal
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Italicize text
     */
    italic?: boolean;
    /**
     * Bold text
     */
    bold?: boolean;
}

declare const TextLegal: React.FC<TextLegalProps>;

export default TextLegal;

