import * as React from 'react';

export type TextSubtitleColor = "white" | "black";

export type TextSubtitleType = "1" | "2" | "3";

export interface TextSubtitleProps {
    /**
     * The text to appear in the subtitle
     */
    children?: React.ReactNode;
    /**
     * To add optional / additional classNames
     */
    className?: string;
    /**
     * Type of HTML element text rendered for Text component
     */
    component: string;
    /**
     * Color of font
     */
    color?: TextSubtitleColor;
    /**
     * Italicize text
     */
    italic?: boolean;
    /**
     * Which type of subtitle style is wanted
     */
    type?: TextSubtitleType;
}

declare const TextSubtitle: React.FC<TextSubtitleProps>;

export default TextSubtitle;

