import * as React from 'react';

export type ToggleSwitchLabelWeight = "regular" | "bold";

export interface ToggleSwitchProps {
    /**
     * Set the checked state of the ToggleSwitch
     */
    checked?: boolean;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Set the name of the input tag
     */
    name: string;
    /**
     * onChange callback function
     */
    onChange?: (...args: any[])=>any;
    /**
     * Set the id of the input tag
     */
    id: string;
    /**
     * Set the label of the ToggleSwitch
     */
    label: React.ReactNode;
    /**
     * Set the label weight of the ToggleSwitch
     */
    labelWeight?: ToggleSwitchLabelWeight;
    /**
     * Set the ToggleSwitch to the disabled state
     */
    disabled?: boolean;
    /**
     * Set the checked state icon, if different from the default checkmark
     */
    CheckIcon?: (...args: any[])=>any;
    /**
     * Set the unchecked state icon, if different from the default x icon
     */
    UncheckIcon?: (...args: any[])=>any;
}

declare const ToggleSwitch: React.FC<ToggleSwitchProps>;

export default ToggleSwitch;

