import * as React from 'react';

export type UnorderedListSize = 16 | 18;

export interface UnorderedListProps {
    /**
     * The children within the unordered list
     */
    children?: React.ReactNode;
    /**
     * Set the font size. If not specified, it inherits from parent
     */
    size?: UnorderedListSize;
    /**
     * Specify whether this unordered list is nested inside of another list
     */
    nested?: boolean;
    /**
     * Specify an optional className to apply to the underlying ul node
     */
    className?: string;
}

declare const UnorderedList: React.FC<UnorderedListProps>;

export default UnorderedList;

