import * as React from 'react';

export interface ErrorProps {
    /**
     * unique id of component
     */
    id?: string;
    /**
     * Optional / additional class name
     */
    className?: string;
    /**
     * 'Error' translated string for screen readers
     */
    errorLabel: string;
    /**
     * Error message that will be displayed if in error state
     */
    errorMsg?: string;
}

declare const Error: React.FC<ErrorProps>;

export default Error;

