import * as React from 'react';

export interface ModalProps {
    /**
     * Elements inside the modal
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Message inside the modal
     */
    message?: React.ReactNode;
    /**
     * Headline inside the modal
     */
    headline: string;
    /**
     * Define the visibility for the Modal
     */
    isModalVisible?: boolean;
    /**
     * Allows only close the modal using the button
     */
    isOnlyClosedByButton?: boolean;
    /**
     * Function to be executed when the user clicks outside of the modal to close it (the overlay)
     */
    onOverlayClick?: (...args: any[])=>any;
    /**
     * Function to update the visibility of Modal
     */
    setModalVisible: (...args: any[])=>any;
}

declare const Modal: React.FC<ModalProps>;

export default Modal;

